# Scanner i2c en MicroPython | MicroPython i2c scannerimport machine#esp8266#i2c = machine.I2C(scl=machine.Pin(5), sda=machine.Pin(4))#esp32 HW_I2C0 scl=18 sda=19, HW_I2C1 scl=25 sda=26i2c = machine.I2C(scl=machine.Pin(18), sda=machine.Pin(19))print('i2c_bus taraniyor...')devices = i2c.scan()if len(devices) == 0:  print("i2c aygit bulunamadi!")else:  print('i2c aygit bulundu:',len(devices))  for device in devices:      print("Decimal address: ",device," | Hexa address: ",hex(device))